#!/usr/bin/env bash

# Immediately catch all errors
set -eo pipefail

# Uncomment for debugging
# set -x
# env

# Autogenerate slicerrc
cat << 'EOF' > ~/.slicerrc
# ~/.slicerrc: autogenerated via dev container lifecycle scripts
# see .devcontainer/post-create-command.sh (in the Slicer repo)
# MANUAL CHANGES ARE NOT PERSISTENT

# Set NVIDIA environment variables
# Some hybrid graphic laptops require the following settings to offload graphics rendering task to NVIDIA and load the GLX library 
if command -v nvidia-smi &> /dev/null && nvidia-smi -L &> /dev/null; then
    export __NV_PRIME_RENDER_OFFLOAD=1
    export __NV_PRIME_RENDER_OFFLOAD_PROVIDER=NVIDIA-G0
    export __GLX_VENDOR_LIBRARY_NAME=nvidia
    export __VK_LAYER_NV_optimus=NVIDIA_only
else
    : # no-op
fi

# Aliases for manging workspace
alias bows='$OVERLAY_WS/src/Slicer/.devcontainer/update-content-command.sh'
alias cows='cd $OVERLAY_WS'

EOF

# Check if ~/.bashrc is patched, otherwise apply it
if [ ! -f ~/.bashrc ] || ! grep -q 'slicerrc' ~/.bashrc; then
    cp /etc/skel/.bashrc ~/
    cat << 'EOF' >> ~/.bashrc

# Source autogenerated slicerrc
. ~/.slicerrc
EOF
fi
